import 'dart:async';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:intl/intl.dart';class DigitalClock extends StatefulWidget {  const DigitalClock({super.key});  @override  _DigitalClockState createState() => _DigitalClockState();}class _DigitalClockState extends State<DigitalClock> {  late String timeString;  late String dateString;  late String dayString;  late Timer timer;  @override  void initState() {    super.initState();    updateTime();    timer = Timer.periodic(Duration(seconds: 1), (Timer t) => updateTime());  }  void updateTime() {    final now = DateTime.now();    final timeFormat = DateFormat('hh:mm:ss a');    final dateFormat = DateFormat('MMMM dd yyyy');    final dayFormat = DateFormat('EEEE');    setState(() {      timeString = timeFormat.format(now);      dateString = dateFormat.format(now);      dayString = dayFormat.format(now);    });  }  @override  void dispose() {    timer.cancel();    super.dispose();  }  TextStyle get textStyle => TextStyle(    color: Colors.white,    fontSize: 48,    fontFamily: 'Times New Roman',    fontWeight: FontWeight.bold,  );  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.black,      body: Center(        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: <Widget>[            Text(timeString, style: textStyle),            Text(dateString, style: textStyle.copyWith(fontSize: 24)),            Text(dayString, style: textStyle.copyWith(fontSize: 24)),          ],        ),      ),    );  }}